import React, { useState } from 'react';
import Modal from 'react-modal';

// Set the app element for accessibility
Modal.setAppElement('#root');

const customModalStyles = {
    content: {
      width: '850px', // Set the desired width
      height: '700px', // Set the desired height
      margin: 'auto', // Center the modal horizontally
    },
  };

const AddRequestUser = () => {
  const [isOpen, setIsOpen] = useState(false);

  const openModal = () => {
    setIsOpen(true);
  };

  const closeModal = () => {
    setIsOpen(false);
  };

  return (
    <div>
      <button onClick={openModal}>Open Popup</button>

      <Modal isOpen={isOpen} onRequestClose={closeModal} style={customModalStyles}>
        <h2>Popup Window</h2>
        <p>This is a pop-up window!</p>
        <button onClick={closeModal}>Close</button>
      </Modal>
    </div>
  );
};

export default AddRequestUser;
