import React, { useState } from "react";
import { FormControl, InputLabel, Select, MenuItem } from "@mui/material";

const CategoryDropdown = ({ label, onOptionChange }) => {
  const [selectedOption, setSelectedOption] = useState("");

  const handleOptionChange = (event) => {
    const selectedValue = event.target.value;
    setSelectedOption(selectedValue);
    onOptionChange(selectedValue);
  };

  return (
    <FormControl
      variant="filled"
      size="small"
      style={{ width: "100%", height: "50%" }}
    >
      <InputLabel id="dropdown-label">{label}</InputLabel>
      <Select
        labelId="dropdown-label"
        id="dropdown"
        value={selectedOption}
        onChange={handleOptionChange}
      >
        <MenuItem value="part and machinery">Part & Machinery</MenuItem>
        <MenuItem value="building">Building</MenuItem>
        <MenuItem value="fabrication">Fabrication</MenuItem>
        <MenuItem value="health and safety">Health & Safety</MenuItem>
        <MenuItem value="furniture">Furniture</MenuItem>
        <MenuItem value="other">Other</MenuItem>
      </Select>
    </FormControl>
  );
};

export default CategoryDropdown;
