import React, { useState }  from 'react';

import { Button } from '@mui/material';


export default function StatusButton (props){
    const status = props.status;


    if (status=='pending'){
        return(
            <Button variant='contained' style={{backgroundColor:'#DDE378', color:'#7B7E3C', height:'25px', width:'150px'}}>Pending</Button>

        );
    }

    if (status=='staff assigned'){
        return(
            <Button variant='contained' style={{backgroundColor:'#68DFDE', color:'#2A6261', height:'25px', width:'150px', fontSize:'80%'}}>Staff Assigned</Button>

        );
    }
}
